import './App.css';
import React from 'react';

class App extends React.Component {
  constructor() {
    super();

    this.handleClick = this.handleClick.bind(this);
    this.state = {
      btn1: 0,
      btn2: 0,
      btn3: 0,
    }
  }

  btnClick(event) {
    this.setState((prevState, _props) => ({
      [event.target.id]: prevState.cliques + 1,
    }));
  }

  render() {
    return (
      <div>
        <button id="btn1" onClick={this.btnClick}>Btn 1 {this.state.btn1}</button>
        <button id="btn2" onClick={this.btnClick}>Btn 2 {this.state.btn2}</button>
        <button id="btn3" onClick={this.btnClick}>Btn 3 {this.state.btn3}</button>
      </div>
    );
  }
}

export default App;
