import React from "react";
import GenericButton from "./GenericInput";
import GenericSelect from "./GenericSelect";
import country from '../country';

class Form extends React.Component {
  constructor() {
    super();

    this.handleInput = this.handleInput.bind(this);

    this.state = {
      name: "",
      email: "",
      cpf: "",
      adress: "",
      city: "",
    };
  }

  handleInput({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <form className="form">
        <fieldset className="fieldset">
          <legend>Dados pessoais</legend>
          <GenericButton
            className="input-form"
            onChange={this.handleInput}
            name="name"
            description="Nome:"
            type="text"
            maxLength="40"
            value= { this.state.name }
          />

          <GenericButton
            className="input-form"
            onChange={this.handleInput}
            name="email"
            description="E-mail:"
            type="email"
            maxLength="50"
            value={this.state.email}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="cpf"
            description="CPF: "
            type="text"
            maxLength="11"
            value={this.state.cpf}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="adress"
            type="text"
            description="EndereÃ§o: "
            maxLength="200"
            value={this.state.adress}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="city"
            type="text"
            description="Cidade: "
            maxLength="28"
            value={this.state.city}
          />

          <GenericSelect 
            className="input-form"
            name="country"
            description="Estado: "
          > 
            <option>Selecione</option>
          </GenericSelect>
        </fieldset>
      </form>
    );
  }
}

export default Form;
