import React from "react";
import GenericButton from "./GenericInput";

class Form extends React.Component {
  constructor() {
    super();

    this.handleInput = this.handleInput.bind(this);

    this.state = {
      name: "",
      email: "",
    };
  }

  handleInput({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <form>
        <fieldset>
          <GenericButton
            onChange={this.handleInput}
            name="name"
            description="Nome:"
            type="text"
            maxLength="40"
            value= { this.state.name }
          />

          <GenericButton
            onChange={this.handleInput}
            name="email"
            description="E-mail:"
            type="email"
            maxLength="50"
            value={this.state.email}
          />
        </fieldset>
      </form>
    );
  }
}

export default Form;
