import React from "react";

class GenericInput extends React.Component {
  render() {
    const { description, type, maxLength, name, onChange} = this.props;

    return(
      <label>
        { description }
        <input 
          type={type} 
          maxLength={ maxLength } 
          name={ name } 
          onChange= { onChange }
        />
      </label>
    );
  }
}

export default GenericInput;
