import { Component } from "react";

class TextArea extends Component {
  render() {
    const { name, className, maxLength, onMouseEnter } = this.props;

    return (
      <label htmlFor={name}>
        <textarea 
        className={className}
        name={name}
        maxLength={maxLength}
        onMouseEnter={onMouseEnter}
      />
      </label>
    );
  }
}

export default TextArea;
