import { Component } from 'react';

class Button extends Component {
  render() {
    const { type, description, className, onClick } = this.props;

    return(
      <section className="submit-container">
        <button 
          className={className}
          type={type}
          onClick={onClick}
        >
          {description} 
        </button>
      </section>
    );
  }
}

export default Button;
