import React from "react";
import GenericButton from "./GenericInput";
import GenericSelect from "./GenericSelect";
import country from '../country';
import GenericInput from "./GenericInput";

class Form extends React.Component {
  constructor() {
    super();

    this.handleInput = this.handleInput.bind(this);

    this.state = {
      name: "",
      email: "",
      cpf: "",
      adress: "",
      city: "",
      country: "",
      complement: "",
    };
  }

  handleInput({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <form className="form">
        <fieldset className="fieldset">
          <legend>Dados pessoais</legend>
          <GenericButton
            className="input-form"
            onChange={this.handleInput}
            name="name"
            description="Nome:"
            type="text"
            maxLength="40"
            value= { this.state.name }
          />

          <GenericButton
            className="input-form"
            onChange={this.handleInput}
            name="email"
            description="E-mail:"
            type="email"
            maxLength="50"
            value={this.state.email}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="cpf"
            description="CPF: "
            type="text"
            maxLength="11"
            value={this.state.cpf}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="adress"
            type="text"
            description="Endereço: "
            maxLength="200"
            value={this.state.adress}
          />

          <GenericButton 
            className="input-form"
            onChange={this.handleInput}
            name="city"
            type="text"
            description="Cidade: "
            maxLength="28"
            value={this.state.city}
          />

          <GenericSelect 
            className="input-form"
            name="country"
            description="Estado: "
            listCountry= { country.map((country) => <option> {country} </option> ) }
            onChange={this.handleInput}
          />

          <section className="input-format"> 
            <GenericInput 
              className="input-format"
              onChange={this.handleInput}
              name="house"
              type="radio"
              description="Complemento: "
              value={this.state.complement}
            /> Casa

            <GenericButton 
              className="input-format"
              onChange={this.handleInput}
              name="house"
              type="radio"
              value={this.state.complement}
            /> Apartamento
          </section>
        </fieldset>
      </form>
    );
  }
}

export default Form;
