import React from 'react';

class GenericSelect extends React.Component {
  render(){
    const { name, description, className } = this.props;

    return (
      <label htmlFor={ name }>
        { description }
        <select name={ name } className= { className }>

        </select>
      </label>
    );
  }
}

export default GenericSelect;
