import React from 'react';
import GenericButton from './GenericInput';

class Form extends React.Component {
  constructor() {
    super();

    this.handleInput = this.handleInput.bind(this);

    this.state = {
      name: '',
    }
  }

  handleInput() {

  }

  render() {
    return (
      <form>
        <fieldset>
          <GenericButton 
            onChange = { this.handleInput }
            name = 'name'
            description = 'Nome:'
            type = 'text'
            maxLength = '40'
          />
        </fieldset>
      </form>
    );
  }
}

export default Form;
